import randomi = 5computer_win = 0user_win = 0draw_times = 0images = ['👊🏻', '✋🏻', '🖖🏻']for i in range(3):    user_choice = int(input("What do you choose? Type 0 for Rock, 1 for Paper or 2 for Scissors"))    if user_choice > 2 or user_choice < 0:        computer_win += 1        print("You typed an invalid number, you lose.")    else:        computer_choice = random.randint(0, 2)        print(f"Computer choose {images[computer_choice]}\n")        print(f"You choose {images[user_choice]}\n")        if user_choice == computer_choice:            draw_times += 1            print("Draw")        elif user_choice - computer_choice != 2:            user_win += 1            print("You win!")        else:            computer_win += 1            print("You lose!")print(f"User win {user_win} times")print(f"Draw {draw_times} times")print(f"Computer win {computer_win} times")print("Game over")